// src/services/newsService.js
import apiClient from './apiClient';
import { createFileUploadConfig } from './apiClient';

const API_BASE_PATH = '/api/v1/news';

const newsService = {
  // Get all news articles
  getAllNews: async () => {
    try {
      const response = await apiClient.get(API_BASE_PATH); // Đổi tên biến thành 'response'
      // Phản hồi API của bạn là một MẢNG TRỰC TIẾP trong thuộc tính 'data' của Axios response.
      // Dựa trên ảnh chụp màn hình, phản hồi JSON là một mảng các đối tượng tin tức.
      // Ví dụ: response.data = [{"id": 25, ...}, {"id": 26, ...}]
      if (Array.isArray(response.data)) { // Kiểm tra xem response.data có phải là một mảng không
        return response.data; // <-- Trả về trực tiếp mảng tin tức
      } else {
        console.error("Unexpected API response structure for getAllNews:", response.data);
        return []; // Trả về mảng rỗng nếu không đúng định dạng
      }
    } catch (error) {
      console.error("Error fetching all news:", error);
      throw error;
    }
  },

  // Get news by ID
  getNewsById: async (id) => {
    try {
      const response = await apiClient.get(`${API_BASE_PATH}/${id}`);
      // Giả định API chi tiết cũng trả về đối tượng tin tức trong thuộc tính 'data'
      return response.data;
    } catch (error) {
      console.error(`Error fetching news with ID ${id}:`, error);
      throw error;
    }
  },

  createNews: async (newsData) => {
    try {
      const response = await apiClient.post(API_BASE_PATH, newsData);
      return response.data;
    } catch (error) {
      console.error("Error creating news:", error);
      throw error;
    }
  },

  // Update a news article
  updateNews: async (id, newsData) => {
    try {
      const response = await apiClient.put(`${API_BASE_PATH}/${id}`, newsData);
      return response.data;
    } catch (error) {
      console.error(`Error updating news with ID ${id}:`, error);
      throw error;
    }
  },

  // Delete a news article
  deleteNews: async (id) => {
    try {
      const response = await apiClient.delete(`${API_BASE_PATH}/${id}`);
      return response.data;
    } catch (error) {
      console.error(`Error deleting news with ID ${id}:`, error);
      throw error;
    }
  },

  uploadNewsAttachments: async (newsId, file, onUploadProgress) => {
    try {
      const formData = new FormData();
      formData.append('files', file);

      const config = createFileUploadConfig(onUploadProgress);
      const response = await apiClient.post(`${API_BASE_PATH}/uploads/${newsId}`, formData, config);
      return response.data;
    } catch (error) {
      console.error(`Error uploading attachment for news ID ${newsId}:`, error);
      throw error;
    }
  },

  getImageUrl: (imageName) => {
    return `${process.env.REACT_APP_API_URL || 'http://localhost:8080'}${API_BASE_PATH}/image/${imageName}`;
  }
};

export default newsService;
