import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { 
  Users, 
  Trophy, 
  Calendar, 
  FileText, 
  Settings,
  Plus,
  Edit,
  Trash2,
  Eye,
  Search,
  Filter,
  UserCheck,
  UserX,
  Shield,
  Crown,
  AlertTriangle
} from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import { tournamentService, newsService } from '../services';
import LoadingSpinner from '../components/LoadingSpinner';
import { formatDate, getStatusColor } from '../utils/helpers';
import toast from 'react-hot-toast';

// Import components with error boundary
let UserManagement, TournamentManagement, MatchManagement, NewsManagement;

try {
  UserManagement = require('../components/admin/UserManagement').default;
} catch (error) {
  console.warn('UserManagement component failed to load:', error);
  UserManagement = () => <div className="p-8 text-center text-gray-500">User Management temporarily unavailable</div>;
}

try {
  TournamentManagement = require('../components/admin/TournamentManagement').default;
} catch (error) {
  console.warn('TournamentManagement component failed to load:', error);
  TournamentManagement = () => <div className="p-8 text-center text-gray-500">Tournament Management temporarily unavailable</div>;
}

try {
