// src/components/admin/CreateNewsModal.js
import React, { useState, useRef, useEffect } from 'react';
import { useMutation, useQueryClient } from 'react-query';
import { X, Image, Trash2 } from 'lucide-react'; // Thêm Trash2 cho nút xóa ảnh
import newsService from '../../services/newsService';
import toast from 'react-hot-toast';
import LoadingSpinner from '../LoadingSpinner';

const CreateNewsModal = ({ show, onClose, onNewsCreated }) => {
  const [newsForm, setNewsForm] = useState({
    name: '',
    shortDescription: '',
    content: ''
  });
  // THAY ĐỔI 1: Sử dụng mảng để lưu trữ nhiều file
  const [selectedFiles, setSelectedFiles] = useState([]);
  const fileInputRef = useRef(null);
  const queryClient = useQueryClient();

  useEffect(() => {
    if (!show) {
      resetForm();
    }
  }, [show]);

  const createNewsMutation = useMutation(
    async ({ newsData, files }) => { // THAY ĐỔI 4a: files là một mảng
      const createdNewsResponse = await newsService.createNews(newsData);
      const newsId = createdNewsResponse?.id ?? createdNewsResponse?.data?.id;

      if (!newsId) {
        throw new Error("News ID is missing after creation. Failed to upload attachments.");
      }

      // THAY ĐỔI 4b: Lặp qua từng file để upload nếu có
      if (files && files.length > 0) {
        // newsService.uploadNewsAttachments cần được cập nhật để chấp nhận một mảng files
        // hoặc bạn sẽ gọi nó nhiều lần cho mỗi file.
        // Để đơn giản hóa, ta giả định service đã được cập nhật để nhận một mảng files.
        await newsService.uploadNewsAttachments(newsId, files);
      }
      return createdNewsResponse;
    },
    {
      onSuccess: () => {
        toast.success('News article created successfully' + (selectedFiles.length > 0 ? ' and attachments uploaded!' : '!'));
        queryClient.invalidateQueries('admin-news');
        queryClient.invalidateQueries('news');
        onClose();
        onNewsCreated && onNewsCreated();
      },
      onError: (error) => {
        console.error("Error in createNewsMutation:", error);
        if (error.message && error.message.includes("News ID is missing")) {
          toast.error('Lỗi: Không thể tạo tin tức hoặc lấy ID để tải ảnh lên. Vui lòng thử lại.');
        } else {
          toast.error(`Failed to create news article: ${error.message || 'Please try again.'}`);
        }
      }
    }
  );

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setNewsForm(prev => ({ ...prev, [name]: value }));
  };

  // THAY ĐỔI 3: Xử lý nhiều file được chọn
  const handleFileChange = (e) => {
    const files = Array.from(e.target.files); // Chuyển FileList thành mảng
    setSelectedFiles(prevFiles => [...prevFiles, ...files]); // Thêm file mới vào mảng
  };

  // Hàm để xóa một ảnh đã chọn (nếu người dùng đổi ý)
  const handleRemoveFile = (indexToRemove) => {
    setSelectedFiles(prevFiles => prevFiles.filter((_, index) => index !== indexToRemove));
  };


  const handleCreateNews = (e) => {
    e.preventDefault();

    if (!newsForm.name || newsForm.name.trim() === '') {
      toast.error('Tên tin tức không được để trống.');
      return;
    }
    if (!newsForm.content || newsForm.content.trim() === '') {
      toast.error('Nội dung tin tức không được để trống.');
      return;
    }

    // THAY ĐỔI 4c: Truyền mảng files vào mutate
    createNewsMutation.mutate({ newsData: newsForm, files: selectedFiles });
  };

  // THAY ĐỔI 6: Reset form và clear tất cả files đã chọn
  const resetForm = () => {
    setNewsForm({
      name: '',
      shortDescription: '',
      content: ''
    });
    setSelectedFiles([]); // Reset mảng files
    if (fileInputRef.current) {
      fileInputRef.current.value = ''; // Xóa giá trị input file
    }
  };

  if (!show) {
    return null;
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-2xl w-full mx-auto max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-2xl font-bold text-gray-900">Create New News Article</h3>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700 transition-colors"
            >
              <X className="h-6 w-6" />
            </button>
          </div>
          <form onSubmit={handleCreateNews} className="space-y-4">
            <div>
              <label htmlFor="modal-name" className="block text-sm font-medium text-gray-700 mb-2">
                Article Name <span className="text-red-500">*</span>
              </label>
              <input
                id="modal-name"
                name="name"
                type="text"
                required
                className="input-field"
                placeholder="Enter article name..."
                value={newsForm.name}
                onChange={handleFormChange}
              />
            </div>

            <div>
              <label htmlFor="modal-shortDescription" className="block text-sm font-medium text-gray-700 mb-2">
                Short Description (Optional)
              </label>
              <textarea
                id="modal-shortDescription"
                name="shortDescription"
                rows={3}
                className="input-field"
                placeholder="Brief summary of the article..."
                value={newsForm.shortDescription}
                onChange={handleFormChange}
              />
            </div>

            <div>
              <label htmlFor="modal-content" className="block text-sm font-medium text-gray-700 mb-2">
                Article Content <span className="text-red-500">*</span>
              </label>
              <textarea
                id="modal-content"
                name="content"
                rows={8}
                required
                className="input-field"
                placeholder="Write your article content here..."
                value={newsForm.content}
                onChange={handleFormChange}
              />
            </div>

            {/* THAY ĐỔI 2: File Upload Section (hỗ trợ nhiều file) */}
            <div>
              <label htmlFor="modal-file-upload" className="block text-sm font-medium text-gray-700 mb-2">
                Upload Images (Select multiple files, image format only)
              </label>
              <button type="button"
                      onClick={() => fileInputRef.current?.click()}
                      className="btn btn-outline-primary btn-sm mb-2"
              >
                Chọn ảnh...
              </button>
              <input
                id="modal-file-upload"
                type="file"
                accept="image/*"
                multiple // THAY ĐỔI 2a: Thêm thuộc tính 'multiple'
                onChange={handleFileChange}
                ref={fileInputRef}
                hidden
              />

              {/* THAY ĐỔI 5: Hiển thị danh sách ảnh đã chọn */}
              {selectedFiles.length > 0 && (
                <div className="mt-2 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2">
                  {selectedFiles.map((file, index) => (
                    <div key={index} className="relative w-24 h-24 border border-gray-300 rounded-md overflow-hidden">
                      <img
                        src={URL.createObjectURL(file)} // Tạo URL tạm thời để hiển thị ảnh
                        alt={`Preview ${index}`}
                        className="w-full h-full object-cover"
                      />
                      <button
                        type="button"
                        onClick={() => handleRemoveFile(index)}
                        className="absolute top-1 right-1 bg-red-600 text-white rounded-full p-0.5 opacity-80 hover:opacity-100 transition-opacity"
                        title="Remove image"
                      >
                        <X className="h-3 w-3" />
                      </button>
                    </div>
                  ))}
                </div>
              )}
               {selectedFiles.length === 0 && (
                 <div className="mt-2 text-sm text-gray-500">
                    Chưa có ảnh nào được chọn.
                 </div>
               )}
            </div>

            {/* Action Buttons in Modal */}
            <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3 pt-4">
              <button
                type="button"
                onClick={onClose}
                className="flex-1 btn-secondary"
              >
                Hủy
              </button>
              <button
                type="submit"
                disabled={createNewsMutation.isLoading}
                className="flex-1 btn-primary disabled:opacity-50"
              >
                {createNewsMutation.isLoading ? (
                  <div className="flex items-center justify-center space-x-2">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                    <span>Tạo...</span>
                  </div>
                ) : (
                  'Tạo'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreateNewsModal;
