// src/components/admin/TeamManagement.jsx
import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Plus, Edit, Trash2, Search, Filter, Users } from 'lucide-react';
import LoadingSpinner from '../LoadingSpinner'; // Đảm bảo đường dẫn này đúng
import Modal from '../Modal'; // Đảm bảo đường dẫn này đúng
import { teamService, tournamentService } from '../../services'; // Import đúng từ file services/index.js
import toast from 'react-hot-toast'; // Đảm bảo bạn đã cài đặt react-hot-toast
import { formatDate } from '../../utils/helpers'; // Đảm bảo đường dẫn này đúng

const TeamManagement = () => {
  const queryClient = useQueryClient();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMode, setModalMode] = useState('add');
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterTournamentId, setFilterTournamentId] = useState(''); // State để lọc theo giải đấu

  // 1. Fetch tournaments for the filter dropdown
  const {
    data: tournaments, // <-- Biến 'tournaments' giờ đây sẽ là một MẢNG trực tiếp
    isLoading: tournamentsLoading,
    isError: tournamentsError,
    error: tournamentsErrorObj
  } = useQuery(
    'tournamentsForTeamFilter', // Key độc đáo cho query này
    async () => {
      // tournamentService.getAllTournaments đã được sửa để TRẢ VỀ MẢNG trực tiếp
      const fetchedTournaments = await tournamentService.getAllTournaments({ limit: 100 });
      console.log("Fetched tournaments for dropdown (in TeamManagement):", fetchedTournaments); // DÒNG LOG QUAN TRỌNG ĐỂ DEBUG
      return fetchedTournaments; // Trả về mảng trực tiếp các đối tượng giải đấu
    },
    {
      staleTime: Infinity, // Dữ liệu giải đấu không thay đổi thường xuyên
      onError: (err) => {
        toast.error(`Error fetching tournaments for filter: ${err.message}`);
      }
    }
  );

  // 2. Tự động chọn giải đấu đầu tiên nếu có dữ liệu và chưa có giải đấu nào được chọn
  useEffect(() => {
    // Đảm bảo tournaments là một mảng và có ít nhất một phần tử
    if (tournaments && tournaments.length > 0 && !filterTournamentId) {
      setFilterTournamentId(tournaments[0].id);
    }
  }, [tournaments, filterTournamentId]);


  // 3. Fetch teams based on selected tournament ID
  const {
    data: teams,
    isLoading: teamsLoading,
    isError: teamsError,
    error: teamsErrorObj
  } = useQuery(
    ['teams', filterTournamentId], // Key phụ thuộc vào filterTournamentId
    // Chỉ gọi hàm này khi filterTournamentId có giá trị (người dùng đã chọn giải đấu)
    () => teamService.getTeamsByTournament(filterTournamentId),
    {
      enabled: !!filterTournamentId, // CHỈ CHẠY QUERY KHI filterTournamentId CÓ GIÁ TRỊ
      staleTime: 60 * 1000, // Dữ liệu đội có thể thay đổi, cache 1 phút
      onError: (err) => {
        toast.error(`Error fetching teams: ${err.message}`);
      }
    }
  );

  // 4. Mutations for Add, Update, Delete operations
  const addTeamMutation = useMutation(teamService.registerTeam, {
    onSuccess: () => {
      // Invalidate queries để refetch danh sách đội của giải đấu hiện tại
      queryClient.invalidateQueries(['teams', filterTournamentId]);
      toast.success('Team registered successfully!');
      setIsModalOpen(false);
    },
    onError: (err) => {
      toast.error(`Error registering team: ${err.message}`);
    },
  });

  const updateTeamMutation = useMutation(
    ({ id, teamData }) => teamService.updateTeam(id, teamData),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['teams', filterTournamentId]);
        toast.success('Team updated successfully!');
        setIsModalOpen(false);
      },
      onError: (err) => {
        toast.error(`Error updating team: ${err.message}`);
      },
    }
  );

  const deleteTeamMutation = useMutation(teamService.deleteTeam, {
    onSuccess: () => {
      queryClient.invalidateQueries(['teams', filterTournamentId]);
      toast.success('Team deleted successfully!');
    },
    onError: (err) => {
      toast.error(`Error deleting team: ${err.message}`);
    },
  });

  // Handlers
  const handleAddTeam = () => {
    setModalMode('add');
    setSelectedTeam(null);
    setIsModalOpen(true);
  };

  const handleEditTeam = (team) => {
    setModalMode('edit');
    setSelectedTeam(team);
    setIsModalOpen(true);
  };

  const handleDeleteTeam = (teamId) => {
    if (window.confirm('Are you sure you want to delete this team?')) {
      deleteTeamMutation.mutate(teamId);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const data = Object.fromEntries(formData.entries());

    // Tạo đối tượng teamData chỉ với các trường mà Backend mong đợi trong TeamRegistrationRequestDTO/TeamUpdateRequestDTO
    const teamDataForApi = {
      name: data.name,
      description: data.description,
      // Thêm các trường khác nếu DTO của bạn có (ví dụ: captainId, memberIds nếu quản lý qua form này)
    };

    if (modalMode === 'add') {
      const tournamentToRegisterId = data.tournamentId || filterTournamentId; // Ưu tiên từ form, nếu không thì từ filter
      if (!tournamentToRegisterId) {
        toast.error('Please select a tournament to add the team to.');
        return;
      }
      addTeamMutation.mutate({ tournamentId: tournamentToRegisterId, ...teamDataForApi });
    } else { // edit mode
      // selectedTeam.id là ID của đội đang được chỉnh sửa
      updateTeamMutation.mutate({ id: selectedTeam.id, teamData: teamDataForApi });
    }
  };

  // Lọc teams theo searchTerm
  const filteredTeams = teams?.filter(team =>
    team.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    team.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Hiển thị trạng thái tải và lỗi
  // Lưu ý: Đặt LoadingSpinner ở đây để cover cả quá trình tải tournaments và teams
  if (tournamentsLoading || teamsLoading) {
    return <LoadingSpinner text="Loading data..." />;
  }

  // Xử lý lỗi khi tải tournaments
  if (tournamentsError) {
    return <div className="text-red-600 p-4">Error loading tournaments: {tournamentsErrorObj.message}</div>;
  }
  // Xử lý lỗi khi tải teams, chỉ hiển thị nếu filterTournamentId đã có giá trị
  if (teamsError && filterTournamentId) {
    return <div className="text-red-600 p-4">Error loading teams: {teamsErrorObj.message}</div>;
  }


  return (
    <div className="p-6 bg-white rounded-lg shadow-sm">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center">
        <Users className="w-6 h-6 mr-2 text-primary-500" /> Team Management
      </h2>

      <div className="flex justify-between items-center mb-6 flex-wrap gap-4">
        <div className="flex items-center space-x-4">
          {/* Search Input */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search teams..."
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          {/* Tournament Filter Dropdown */}
          <div className="relative">
            <Filter className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400 w-5 h-5" />
            <select
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
              value={filterTournamentId}
              onChange={(e) => setFilterTournamentId(e.target.value)}
              disabled={tournamentsLoading} // Vô hiệu hóa khi đang tải
            >
              <option value="">-- Select Tournament --</option>
              {/* Đảm bảo tournaments là một mảng trước khi map */}
              {tournaments && tournaments.length > 0 ? (
                tournaments.map(tourney => (
                  <option key={tourney.id} value={tourney.id}>{tourney.name}</option>
                ))
              ) : (
                // Hiển thị tùy chọn "No tournaments available" nếu không có giải đấu nào
                <option value="" disabled>No tournaments available</option>
              )}
            </select>
          </div>
        </div>
        {/* Add New Team Button */}
        <button
          onClick={handleAddTeam}
          className="flex items-center px-4 py-2 bg-primary-600 text-white rounded-md hover:bg-primary-700 transition-colors focus:outline-none focus:ring-2 focus:ring-primary-500"
        >
          <Plus className="w-5 h-5 mr-2" /> Add New Team
        </button>
      </div>

      {/* Conditional Message for Tournament Selection */}
      {!filterTournamentId && (
        <div className="text-center p-8 bg-blue-50 border border-blue-200 rounded-lg text-blue-800">
          <p className="text-lg font-medium">Please select a tournament to view and manage teams.</p>
        </div>
      )}

      {/* Team Table - Only display when a tournament is selected */}
      {filterTournamentId && (
        <div className="overflow-x-auto bg-gray-50 rounded-lg border border-gray-200 shadow-sm">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-100">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  ID
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Team Name
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Description
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Created At
                </th>
                <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredTeams && filteredTeams.length > 0 ? (
                filteredTeams.map((team) => (
                  <tr key={team.id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {team.id}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {team.name}
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-600">
                      {team.description || 'N/A'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-600">
                      {formatDate(team.createdAt)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex justify-end space-x-2">
                        <button
                          onClick={() => handleEditTeam(team)}
                          className="text-primary-600 hover:text-primary-900 p-2 rounded-full hover:bg-gray-200 transition-colors"
                          title="Edit Team"
                        >
                          <Edit className="w-5 h-5" />
                        </button>
                        <button
                          onClick={() => handleDeleteTeam(team.id)}
                          className="text-red-600 hover:text-red-900 p-2 rounded-full hover:bg-gray-200 transition-colors"
                          title="Delete Team"
                        >
                          <Trash2 className="w-5 h-5" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                    No teams found for the selected tournament.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}

      {/* Team Add/Edit Modal */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} title={modalMode === 'add' ? 'Add New Team' : 'Edit Team'}>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">Team Name</label>
            <input
              type="text"
              id="name"
              name="name"
              defaultValue={selectedTeam?.name || ''}
              required
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
            <textarea
              id="description"
              name="description"
              rows="3"
              defaultValue={selectedTeam?.description || ''}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
            ></textarea>
          </div>
          {modalMode === 'add' && (
            <div>
              <label htmlFor="tournamentId" className="block text-sm font-medium text-gray-700">Assign to Tournament</label>
              <select
                id="tournamentId"
                name="tournamentId"
                // Đặt giá trị mặc định của select box là ID giải đấu đang được lọc nếu có
                defaultValue={filterTournamentId || ''}
                required
                className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
              >
                <option value="">Select a Tournament</option>
                {tournaments && tournaments.length > 0 ? (
                  tournaments.map(tourney => (
                    <option key={tourney.id} value={tourney.id}>{tourney.name}</option>
                  ))
                ) : (
                  <option value="" disabled>No tournaments available</option>
                )}
              </select>
            </div>
          )}
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={() => setIsModalOpen(false)}
              className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-primary-600 text-white rounded-md shadow-sm text-sm font-medium hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
              disabled={addTeamMutation.isLoading || updateTeamMutation.isLoading}
            >
              {addTeamMutation.isLoading || updateTeamMutation.isLoading ? 'Saving...' : (modalMode === 'add' ? 'Add Team' : 'Update Team')}
            </button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default TeamManagement;
