import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { Link } from 'react-router-dom';
import { Search, Calendar, User, Eye, ArrowRight, Plus, Shield } from 'lucide-react';
import { newsService } from '../services';
import { useAuth } from '../context/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import { formatDate, truncateText } from '../utils/helpers';

const NewsPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const { user } = useAuth();

  const isAdmin = user?.role === 'ADMIN';

  const { data: news, isLoading, error, refetch } = useQuery(
    ['news', searchTerm], // Sử dụng key 'news' để sync với invalidation từ admin
    async () => {
      console.log('NewsPage: Fetching news...');
      const result = await newsService.getAllNews();
      console.log('NewsPage: Fetched data:', result);
      return result;
    },
    {
      staleTime: 2 * 60 * 1000, // Giảm staleTime để data fresh hơn
      cacheTime: 5 * 60 * 1000,
      refetchOnWindowFocus: true, // Refetch khi focus lại window
      retry: 3,
      select: (data) => {
        console.log('NewsPage: Processing data in select:', data);

        // Kiểm tra data có tồn tại và là array không
        if (!data) {
          console.log('NewsPage: No data returned');
          return [];
        }

        // Xử lý trường hợp data có thể là object với property data
        let newsArray = Array.isArray(data) ? data : (data.data || []);

        if (!Array.isArray(newsArray)) {
          console.log('NewsPage: Invalid data structure:', data);
          return [];
        }

        console.log('NewsPage: Valid news array:', newsArray);

        // Lọc theo search term nếu có
        if (!searchTerm.trim()) {
          return newsArray;
        }

        const filtered = newsArray.filter(article => {
          if (!article) return false;

          const title = article.name || article.title || '';
          const content = article.content || '';
          const shortDescription = article.shortDescription || '';

          const searchLower = searchTerm.toLowerCase();

          return title.toLowerCase().includes(searchLower) ||
                 content.toLowerCase().includes(searchLower) ||
                 shortDescription.toLowerCase().includes(searchLower);
        });

        console.log('NewsPage: Filtered results:', filtered);
        return filtered;
      },
      onError: (error) => {
        console.error('NewsPage: Query error:', error);
      },
      onSuccess: (data) => {
        console.log('NewsPage: Query success, data length:', data?.length || 0);
      }
    }
  );

  const handleSearch = (e) => {
    e.preventDefault();
    // Trigger refetch nếu cần
    refetch();
  };

  // Debug: Log current state
  console.log('NewsPage render:', { 
    isLoading, 
    error: error?.message, 
    newsLength: news?.length,
    searchTerm,
    news: news?.slice(0, 2) // Log first 2 items for debugging
  });

  if (error) {
    console.error('NewsPage error:', error);
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-red-600">
          <p>Error loading news. Please try again later.</p>
          <p className="text-sm text-gray-500 mt-2">Error: {error.message}</p>
          <button 
            onClick={() => refetch()} 
            className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  const featuredNews = news?.[0];
  const otherNews = news?.slice(1) || [];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex items-center justify-between">
            <div className="text-center flex-1">
              <h1 className="text-4xl font-bold text-gray-900 mb-4">News</h1>
              <p className="text-xl text-gray-600 max-w-2xl mx-auto">
                Get the latest sports news, tournament information and community highlights
              </p>
            </div>
            {isAdmin && (
              <div className="flex items-center space-x-3">
                <div className="flex items-center space-x-2 text-sm text-primary-600">
                  <Shield className="h-4 w-4" />
                  <span>Admin Access</span>
                </div>
                <Link
                  to="/admin"
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Manage News
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Search Bar */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <form onSubmit={handleSearch} className="flex gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <input
                  type="text"
                  placeholder="Search news..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 input-field"
                />
              </div>
            </div>
            <button type="submit" className="btn-primary">
              Search
            </button>
          </form>

          {/* Debug info - Remove in production */}
          {process.env.NODE_ENV === 'development' && (
            <div className="mt-4 p-3 bg-gray-100 rounded text-sm">
              <strong>Debug Info:</strong> Loading: {isLoading.toString()}, 
              News Count: {news?.length || 0}, 
              Error: {error?.message || 'None'}
            </div>
          )}
        </div>

        {isLoading ? (
          <div className="text-center py-12">
            <LoadingSpinner />
            <p className="text-gray-500 mt-4">Loading latest news...</p>
          </div>
        ) : !news || news.length === 0 ? (
          <div className="text-center py-12">
            <Calendar className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-xl font-medium text-gray-900 mb-2">
              {searchTerm ? 'No news found matching your search' : 'No news available'}
            </h3>
            <p className="text-gray-600 mb-4">
              {searchTerm 
                ? 'Try searching with different keywords' 
                : 'Check back later for the latest sports updates'
              }
            </p>
            {searchTerm && (
              <button
                onClick={() => setSearchTerm('')}
                className="text-primary-600 hover:text-primary-700 font-medium"
              >
                Clear search
              </button>
            )}
            {isAdmin && !searchTerm && (
              <Link
                to="/admin"
                className="inline-flex items-center mt-4 px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
              >
                <Plus className="h-4 w-4 mr-2" />
                Create first news
              </Link>
            )}
          </div>
        ) : (
          <div className="space-y-8">
            {/* Featured Article */}
            {featuredNews && (
              <div className="bg-white rounded-xl shadow-sm overflow-hidden">
                <div className="md:flex">
                  <div className="md:flex-shrink-0">
                    {featuredNews.attachments && featuredNews.attachments.length > 0 ? (
                      <div className="h-48 w-full md:w-64 bg-gray-200 overflow-hidden">
                        <img
                          src={newsService.getImageUrl(featuredNews.attachments[0].url)}
                          alt={featuredNews.name || featuredNews.title}
                          className="w-full h-full object-cover"
                          onError={(e) => { 
                            e.target.onerror = null; 
                            e.target.style.display = 'none';
                            e.target.parentElement.innerHTML = '<div class="h-48 w-full md:w-64 bg-gradient-to-r from-primary-500 to-purple-500 flex items-center justify-center"><svg class="h-16 w-16 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v14a2 2 0 002 2z"></path></svg></div>';
                          }}
                        />
                      </div>
                    ) : (
                      <div className="h-48 w-full md:w-64 bg-gradient-to-r from-primary-500 to-purple-500 flex items-center justify-center">
                        <Calendar className="h-16 w-16 text-white" />
                      </div>
                    )}
                  </div>
                  <div className="p-8 flex-1">
                    <div className="flex items-center text-sm text-gray-500 mb-2">
                      <span className="bg-primary-100 text-primary-800 px-2 py-1 rounded-full text-xs font-medium mr-3">
                        Featured
                      </span>
                      <Calendar className="h-4 w-4 mr-1" />
                      <span>{formatDate(featuredNews.createdAt)}</span>
                      <User className="h-4 w-4 ml-4 mr-1" />
                      <span>{featuredNews.author || 'EduSports Team'}</span>
                    </div>
                    <h2 className="text-2xl font-bold text-gray-900 mb-3">
                      <Link 
                        to={`/news/${featuredNews.id}`}
                        className="hover:text-primary-600 transition-colors"
                      >
                        {featuredNews.name || featuredNews.title}
                      </Link>
                    </h2>
                    <p className="text-gray-600 mb-4 text-lg leading-relaxed">
                      {truncateText(featuredNews.shortDescription || featuredNews.content, 200)}
                    </p>
                    <Link
                      to={`/news/${featuredNews.id}`}
                      className="inline-flex items-center text-primary-600 hover:text-primary-700 font-medium"
                    >
                      Read full article
                      <ArrowRight className="h-4 w-4 ml-1" />
                    </Link>
                  </div>
                </div>
              </div>
            )}

            {/* Other Articles Grid */}
            {otherNews.length > 0 && (
              <div>
                <h2 className="text-2xl font-bold text-gray-900 mb-6">More Articles</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {otherNews.map((article) => (
                    <article key={article.id} className="card hover:shadow-lg transition-shadow duration-300">
                      {article.attachments && article.attachments.length > 0 ? (
                        <div className="bg-gray-200 h-40 rounded-lg mb-4 overflow-hidden">
                          <img
                            src={newsService.getImageUrl(article.attachments[0].url)}
                            alt={article.name || article.title}
                            className="w-full h-full object-cover"
                            onError={(e) => { 
                              e.target.onerror = null; 
                              e.target.style.display = 'none';
                              e.target.parentElement.innerHTML = '<div class="bg-gradient-to-r from-green-400 to-pink-400 h-40 rounded-lg mb-4 flex items-center justify-center"><svg class="h-12 w-12 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v14a2 2 0 002 2z"></path></svg></div>';
                            }}
                          />
                        </div>
                      ) : (
                        <div className="bg-gradient-to-r from-green-400 to-pink-400 h-40 rounded-lg mb-4 flex items-center justify-center">
                          <Calendar className="h-12 w-12 text-white" />
                        </div>
                      )}

                      <div className="flex items-center text-sm text-gray-500 mb-2">
                        <Calendar className="h-4 w-4 mr-1" />
                        <span>{formatDate(article.createdAt)}</span>
                        <User className="h-4 w-4 ml-4 mr-1" />
                        <span>{article.author || 'EduSports Team'}</span>
                      </div>

                      <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">
                        <Link 
                          to={`/news/${article.id}`}
                          className="hover:text-primary-600 transition-colors"
                        >
                          {article.name || article.title}
                        </Link>
                      </h3>

                      <p className="text-gray-600 mb-4 line-clamp-3">
                        {truncateText(article.shortDescription || article.content, 120)}
                      </p>

                      <div className="flex items-center justify-between">
                        <Link
                          to={`/news/${article.id}`}
                          className="text-primary-600 hover:text-primary-700 font-medium text-sm inline-flex items-center"
                        >
                          Read more
                          <ArrowRight className="h-3 w-3 ml-1" />
                        </Link>
                        <div className="flex items-center text-sm text-gray-500">
                          <Eye className="h-4 w-4 mr-1" />
                          <span>0 views</span>
                        </div>
                      </div>
                    </article>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Refresh Button for debugging */}
        {process.env.NODE_ENV === 'development' && (
          <div className="text-center mt-8">
            <button 
              onClick={() => refetch()}
              className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
              Force Refresh Data
            </button>
          </div>
        )}

        {/* Load More Button */}
        {news && news.length >= 10 && (
          <div className="text-center mt-12">
            <button className="btn-secondary">
              Load more articles
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default NewsPage;
